import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  Image,
  ImageBackground,
  YellowBox,
} from "react-native";
import Searchbar from "./components/Searchbar";
import { Ionicons, Entypo } from "@expo/vector-icons";
import { Feather } from "@expo/vector-icons";
import LOGOSVG from "./assets/animated/cloudy.svg";

const im = require("./assets/rain.png");
const image = require("./assets/background.jpg");
export default function App() {
  return (
    <View style={styles.container}>
      <ImageBackground
        source={image}
        resizeMode="cover"
        style={{
          width: "100%",
          height: "100%",
          alignItems: "center",
        }}
      >
        <Searchbar />
        <View
          style={{
            width: "100%",
            flexDirection: "column",
            alignItems: "center",
            padding: 15,
          }}
        >
          <View
            style={{
              flexDirection: "row",
              alignItems: "center",
              justifyContent: "center",
              marginTop: 10,
            }}
          >
            <Ionicons
              name="location-outline"
              size={12}
              color="white"
              style={{ padding: 5 }}
            />
            <Text style={{ fontSize: 14 }}>Kolkata, India</Text>
          </View>

          <View style={{ marginTop: 30 }}>
            <Image
              style={{ width: 150, height: 150, padding: 40 }}
              source={im}
            />
          </View>
          <View
            style={{
              marginTop: 10,
              flexDirection: "row",
              padding: 10,
              alignSelf: "center",
            }}
          >
            <Text style={{ fontSize: 75, color: "white" }}>16</Text>
            <Entypo
              name="circle"
              size={14}
              color="white"
              style={{ marginTop: 10, padding: 5 }}
            />
            <Text style={{ fontSize: 50, marginTop: 25, color: "white" }}>
              C
            </Text>
          </View>
          <View
            style={{
              padding: 10,
              marginTop: -20,
              marginBottom: 10,
              alignItems: "center",
            }}
          >
            <Text
              style={{ alignSself: "center", color: "white", fontSize: 30 }}
            >
              Thunderstorm
            </Text>
            <Text style={{ alignSelf: "center", fontSize: 14, color: "white" }}>
              Friday, 4th November
            </Text>
          </View>
          <View
            style={{
              // width: "90%",
              // backgroundColor: "red",
              flexDirection: "row",
              justifyContent: "space-between",
              padding: 10,
              //  borderWidth:2,
               borderTopWidth:2,
            }}
          >
            <View style={{ flexDirection: "column", alignItems: "center",padding:10}}>
              <Ionicons name='rainy-outline' size={35} color="white" />
              <Text style={{marginTop:5,color:"white"}}>90%</Text>
              <Text style={{marginTop:5,color:"white"}}>Wet Pows</Text>
            </View>
            <View style={{ flexDirection: "column", alignItems: "center",padding:10 }}>
              <Feather name="wind" size={35} color="white" />
              <Text style={{marginTop:5,color:"white"}}>35 km/hr</Text>
              <Text style={{marginTop:5,color:"white"}}>Windy Fur</Text>
            </View>
            <View style={{ flexDirection: "column", alignItems: "center",padding:10 }}>
              <Feather name="sun" size={35} color="white" />
              <Text style={{marginTop:5,color:"white"}}>86%</Text>
              <Text style={{marginTop:5,color:"white"}}>Thunderstorm</Text>
            </View>
          </View>
        </View>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "blue",
    maxHeight: "100%",
    marginTop: StatusBar.CurrentHeight,
    height:"100%",
  },
});
