import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  Image,
  ImageBackground,
} from "react-native";
import Searchbar from "./components/Searchbar";
import { Ionicons,Entypo } from "@expo/vector-icons";
import { Feather } from "@expo/vector-icons";
import LOGOSVG from "./assets/animated/cloudy.svg";

const im = require("./assets/rain.png");
const image = require("./assets/background.jpg");
export default function App() {
  return (
    <View style={styles.container}>
      <ImageBackground
        source={image}
        resizeMode="cover"
        style={{
          width: "100%",
          height: "100%",
          alignItems: "center",
        }}
      >
        <Searchbar />
        <View
          style={{
            width: "100%",
            flexDirection: "column",
            alignItems: "center",
            padding: 15,
          }}
        >
          <Text style={{ marginTop: 10 }}>Kolkata</Text>
          <View style={{ marginTop: 30 }}>
            <Image style={{ width: 150, height: 150, padding: 40 }} source={im} />
          </View>
          <View style={{ marginTop: 10, flexDirection: "row",padding:10,alignSelf:'center'}}>
            <Text style={{ fontSize: 75, color:"white"}}>16</Text>
            <Entypo name="circle" size={14} color="white" style={{marginTop:10,padding:5,}}/>
            <Text style={{fontSize:50,marginTop:24,color:'white'}}>C</Text>
          </View>
          <View style={{padding:10,marginTop:-20,marginBottom:10,alignItems:'center'}}>
            <Text style={{alignSself:'center',color:"white",fontSize:30}}>Thunderstorm</Text>
            <Text style={{alignSelf:'center',fontSize:14,color:"white"}}>Friday, 4th November</Text>
          </View>
          <View
            style={{
              width: "90%",
              backgroundColor: "red",
              flexDirection: "row",
              justifyContent: "space-between",
              padding: 10,
            }}
          >
            <Ionicons name="rainy" size={30} color="black" />
            <Feather name="wind" size={30} color="black" />
            <Feather name="sun" size={30} color="black" />
          </View>
        </View>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "blue",
    maxHeight: "100%",
    marginTop: StatusBar.CurrentHeight,
  },
});
